if SERVER then return endlocal lastsong = ""local listeners = 0local boxalpha = 0local textalpha = 0local fadingin = falselocal fadingout = falsefunction drawhud()	if fadingout and boxalpha == 0.8 and textalpha == 1 then		fadingin = false	end	    if fadingout and boxalpha == 0 and textalpha == 0 then        fadingout = false        return    end    	if fadingin then		boxalpha = math.clamp(boxalpha + 0.05, 0, 0.5)		textalpha = math.clamp(textalpha + 0.05, 0, 1)	end		if fadingout then		boxalpha = math.clamp(boxalpha - 0.05, 0, 0.5)		textalpha = math.clamp(textalpha - 0.05, 0, 1)	end			graphics.DrawRect(Rect(0, 0, 400, 45), Color(0, 0, 0, boxalpha), 8, nil, nil, nil, nil, false, true, false, false)		graphics.DrawText("ukgamer's radio - now playing:", Vec2(10, 10), "arial.ttf", Vec2() + 10, Color(1, 1, 1, textalpha))    graphics.DrawText(lastsong, Vec2(10, 27), "arial.ttf", Vec2() + 12, Color(1, 1, 1, textalpha))		local listenerstxt = ""		if listeners == 1 then		listenerstxt = listeners .. " listener"	else		listenerstxt = listeners .. " listeners"	end		graphics.DrawText(listenerstxt, Vec2(390, 10), "arial.ttf", Vec2() + 10, Color(1, 1, 1, textalpha))endfunction update()	http.Get("http://lab.epic-empires.co.uk/stream.php", function(data)		if data.content == "Stream offline" then			hook.Remove("DrawHUD", "nowplaying")			timer.Destroy("streamtimer")			return		end		local info = string.explode(data.content, "\n")		if info[1] ~= nil and info[1] ~= lastsong then			lastsong = info[1]			fadingin = true			timer.Simple(5, function() fadingin = false fadingout = true end)		end		listeners = tonumber(info[2]) or 0	end)endupdate()hook.Add("DrawHUD", "nowplaying", drawhud)timer.Create("streamtimer", 30, 0, update)